var kdbush = require('kdbush');

module.exports = supercluster;
module.exports.default = supercluster;

function supercluster(options) {
  return new SuperCluster(options);
}

function SuperCluster(options) {
  this.options = extend(Object.create(this.options), options);
  this.trees = new Array(this.options.maxZoom + 1);
}

SuperCluster.prototype = {
  options: {
    minZoom: 0,
    // min zoom to generate clusters on
    maxZoom: 16,
    // max zoom level to cluster the points on
    radius: 40,
    // cluster radius in pixels
    extent: 512,
    // tile extent (radius is calculated relative to it)
    nodeSize: 64,
    // size of the KD-tree leaf node, affects performance
    log: false,
    // whether to log timing info
    // a reduce function for calculating custom cluster properties
    reduce: null,
    // function (accumulated, props) { accumulated.sum += props.sum; }
    // initial properties of a cluster (before running the reducer)
    initial: function initial() {
      return {};
    },
    // function () { return {sum: 0}; },
    // properties to use for individual points when running the reducer
    map: function map(props) {
      return props;
    } // function (props) { return {sum: props.my_value}; },

  },
  load: function load(points) {
    var log = this.options.log;
    if (log) console.time('total time');
    var timerId = 'prepare ' + points.length + ' points';
    if (log) console.time(timerId);
    this.points = points; // generate a cluster object for each point and index input points into a KD-tree

    var clusters = [];

    for (var i = 0; i < points.length; i++) {
      if (!points[i].geometry) {
        continue;
      }

      clusters.push(createPointCluster(points[i], i));
    }

    this.trees[this.options.maxZoom + 1] = kdbush(clusters, getX, getY, this.options.nodeSize, Float32Array);
    if (log) console.timeEnd(timerId); // cluster points on max zoom, then cluster the results on previous zoom, etc.;
    // results in a cluster hierarchy across zoom levels

    for (var z = this.options.maxZoom; z >= this.options.minZoom; z--) {
      var now = +Date.now(); // create a new set of clusters for the zoom and index them with a KD-tree

      clusters = this._cluster(clusters, z);
      this.trees[z] = kdbush(clusters, getX, getY, this.options.nodeSize, Float32Array);
      if (log) console.log('z%d: %d clusters in %dms', z, clusters.length, +Date.now() - now);
    }

    if (log) console.timeEnd('total time');
    return this;
  },
  getClusters: function getClusters(bbox, zoom) {
    if (bbox[0] > bbox[2]) {
      var easternHem = this.getClusters([bbox[0], bbox[1], 180, bbox[3]], zoom);
      var westernHem = this.getClusters([-180, bbox[1], bbox[2], bbox[3]], zoom);
      return easternHem.concat(westernHem);
    }

    var tree = this.trees[this._limitZoom(zoom)];

    var ids = tree.range(lngX(bbox[0]), latY(bbox[3]), lngX(bbox[2]), latY(bbox[1]));
    var clusters = [];

    for (var i = 0; i < ids.length; i++) {
      var c = tree.points[ids[i]];
      clusters.push(c.numPoints ? getClusterJSON(c) : this.points[c.id]);
    }

    return clusters;
  },
  getChildren: function getChildren(clusterId) {
    var originId = clusterId >> 5;
    var originZoom = clusterId % 32;
    var errorMsg = 'No cluster with the specified id.';
    var index = this.trees[originZoom];
    if (!index) throw new Error(errorMsg);
    var origin = index.points[originId];
    if (!origin) throw new Error(errorMsg);
    var r = this.options.radius / (this.options.extent * Math.pow(2, originZoom - 1));
    var ids = index.within(origin.x, origin.y, r);
    var children = [];

    for (var i = 0; i < ids.length; i++) {
      var c = index.points[ids[i]];

      if (c.parentId === clusterId) {
        children.push(c.numPoints ? getClusterJSON(c) : this.points[c.id]);
      }
    }

    if (children.length === 0) throw new Error(errorMsg);
    return children;
  },
  getLeaves: function getLeaves(clusterId, limit, offset) {
    limit = limit || 10;
    offset = offset || 0;
    var leaves = [];

    this._appendLeaves(leaves, clusterId, limit, offset, 0);

    return leaves;
  },
  getTile: function getTile(z, x, y) {
    var tree = this.trees[this._limitZoom(z)];

    var z2 = Math.pow(2, z);
    var extent = this.options.extent;
    var r = this.options.radius;
    var p = r / extent;
    var top = (y - p) / z2;
    var bottom = (y + 1 + p) / z2;
