      clusterZoom++;
      if (children.length !== 1) break;
      clusterId = children[0].properties.cluster_id;
    }

    return clusterZoom;
  },
  _appendLeaves: function _appendLeaves(result, clusterId, limit, offset, skipped) {
    var children = this.getChildren(clusterId);

    for (var i = 0; i < children.length; i++) {
      var props = children[i].properties;

      if (props && props.cluster) {
        if (skipped + props.point_count <= offset) {
          // skip the whole cluster
          skipped += props.point_count;
        } else {
          // enter the cluster
          skipped = this._appendLeaves(result, props.cluster_id, limit, offset, skipped); // exit the cluster
        }
      } else if (skipped < offset) {
        // skip a single point
        skipped++;
      } else {
        // add a single point
        result.push(children[i]);
      }

      if (result.length === limit) break;
    }

    return skipped;
  },
  _addTileFeatures: function _addTileFeatures(ids, points, x, y, z2, tile) {
    for (var i = 0; i < ids.length; i++) {
      var c = points[ids[i]];
      tile.features.push({
        type: 1,
        geometry: [[Math.round(this.options.extent * (c.x * z2 - x)), Math.round(this.options.extent * (c.y * z2 - y))]],
        tags: c.numPoints ? getClusterProperties(c) : this.points[c.id].properties
