    }
  },
  _limitZoom: function _limitZoom(z) {
    return Math.max(this.options.minZoom, Math.min(z, this.options.maxZoom + 1));
  },
  _cluster: function _cluster(points, zoom) {
    var clusters = [];
    var r = this.options.radius / (this.options.extent * Math.pow(2, zoom)); // loop through each point

    for (var i = 0; i < points.length; i++) {
      var p = points[i]; // if we've already visited the point at this zoom level, skip it

      if (p.zoom <= zoom) continue;
      p.zoom = zoom; // find all nearby points

      var tree = this.trees[zoom + 1];
      var neighborIds = tree.within(p.x, p.y, r);
      var numPoints = p.numPoints || 1;
      var wx = p.x * numPoints;
      var wy = p.y * numPoints;
      var clusterProperties = null;

      if (this.options.reduce) {
        clusterProperties = this.options.initial();

        this._accumulate(clusterProperties, p);
      } // encode both zoom and point index on which the cluster originated


      var id = (i << 5) + (zoom + 1);

      for (var j = 0; j < neighborIds.length; j++) {
        var b = tree.points[neighborIds[j]]; // filter out neighbors that are already processed

        if (b.zoom <= zoom) continue;
        b.zoom = zoom; // save the zoom (so it doesn't get processed twice)

        var numPoints2 = b.numPoints || 1;
        wx += b.x * numPoints2; // accumulate coordinates for calculating weighted center

        wy += b.y * numPoints2;
        numPoints += numPoints2;
        b.parentId = id;

        if (this.options.reduce) {
          this._accumulate(clusterProperties, b);
        }
      }

      if (numPoints === 1) {
        clusters.push(p);
      } else {
        p.parentId = id;
        clusters.push(createCluster(wx / numPoints, wy / numPoints, id, numPoints, clusterProperties));
      }
    }

    return clusters;
  },
  _accumulate: function _accumulate(clusterProperties, point) {
    var properties = point.numPoints ? point.properties : this.options.map(this.points[point.id].properties);
    this.options.reduce(clusterProperties, properties);
  }
};

function createCluster(x, y, id, numPoints, properties) {
  return {
    x: x,
    // weighted cluster center
    y: y,
    zoom: Infinity,
    // the last zoom the cluster was processed at
    id: id,
    // encodes index of the first child of the cluster and its zoom level
    parentId: -1,
    // parent cluster id
    numPoints: numPoints,
    properties: properties
  };
}

function createPointCluster(p, id) {
  var coords = p.geometry.coordinates;
  return {
    x: lngX(coords[0]),
    // projected point coordinates
    y: latY(coords[1]),
    zoom: Infinity,
    // the last zoom the point was processed at
    id: id,
    // index of the source feature in the original input array
    parentId: -1 // parent cluster id

  };
}

function getClusterJSON(cluster) {
  return {
    type: 'Feature',
    properties: getClusterProperties(cluster),
    geometry: {
      type: 'Point',
      coordinates: [xLng(cluster.x), yLat(cluster.y)]
    }
  };
}

function getClusterProperties(cluster) {
  var count = cluster.numPoints;
  var abbrev = count >= 10000 ? Math.round(count / 1000) + 'k' : count >= 1000 ? Math.round(count / 100) / 10 + 'k' : count;
  return extend(extend({}, cluster.properties), {
    cluster: true,
    cluster_id: cluster.id,
    point_count: count,
    point_count_abbreviated: abbrev
