import React, {Component } from 'react'
import {Link} from 'react-router-dom'
import jwt_decode from 'jwt-decode'
import SearchShop from './SearchShop'
import AddShop from './AddShop'

import MapGL, {NavigationControl} from 'react-map-gl';

const TOKEN = 'pk.eyJ1IjoicnVhcnRyYW5vc2EiLCJhIjoiY2p1cXRla282MTVkczQzcGl5ZWk5MTNwOCJ9.fkQQbqTMnuvR9Y3sRE_EHg';
const navStyle = {
  position: 'absolute',
  top: 0,
  left: 0,
  padding: '10px'
};

class Profile extends Component {
    constructor() {
        super()
        this.state = {
            username: '',
            display_name: '',
            email: '',
            shop: [],
            shopName: "",
            showAdminTools: false,
            showAddShop: false,
            viewport: {
                type: 'FeatureCollection',
  features: [{
    type: 'Feature',
    geometry: {
      type: 'Point',
      coordinates: [121.226,14.179]
    },
    properties: {
      title: 'Mapbox',
      description: 'Los Banos, Laguna'
    }
  },
  {
    type: 'Feature',
    geometry: {
      type: 'Point',
      coordinates: [121.24256647998524, 14.16052895]
    },
    properties: {
      title: 'Mapbox',
      description: 'UP Los Banos'
    }
  }]
              }
        }

        this.addShop = this.addShop.bind(this)

    }

    componentDidMount () {
        var token = localStorage.usertoken
        console.log(token)
        var decoded = jwt_decode(token)
        console.log("decoded token")
        this.setState({
            username: decoded.username,
            display_name: decoded.display_name,
            email: decoded.email
        })

    }
    
    addShop(){
        if(this.state.showAddShop === false){
            this.setState({showAddShop: true})
            console.log(this.state.showAddShop)
        }else{
            this.setState({showAddShop: false})
            console.log(this.state.showAddShop)
        }
    }


    render () {
        {if(this.state.showAddShop){
            var shop = (
                <AddShop/>
            )
         } 
        const temp = this.state.viewport;
   var map = new MapGL.Map({
container: 'map', // container id
style: 'mapbox://styles/mapbox/streets-v11',
center: [121.226,14.179], // long, lat
zoom: 9 // starting zoom
});
   map.addControl(new MapGL.NavigationControl());

        }
        return (
            <div>
            <MapGL
            {...this.state.viewport}
            mapStyle="mapbox://styles/mapbox/dark-v9"
            mapboxApiAccessToken={TOKEN}>
            <div className="nav" style={navStyle}>
              <NavigationControl/>
            </div>
          </MapGL>
                <table>
                    <tbody>
                    <tr><h2>PROFILE</h2></tr>
                        <tr>
                            <td>Username</td>
                            <td>{this.state.username}</td>
                        </tr>
                        <tr>
                            <td>Display Name</td>
                            <td>{this.state.display_name}</td>
                        </tr>
                        <tr>
                            <td>Email</td>
                            <td>{this.state.email}</td>
                        </tr>
                    </tbody>
                </table>
                
               

                <button onClick = {this.addShop}
                    className = "search-button">
                      Add Shop
                </button>  
                {shop}
                <SearchShop/>
            </div>
        )
    }
}

export default Profile
